mapper.select:
SELECT u_id, u_name, u_email, u_zip_code, u_addr, u_addrdetail, u_phone, u_regdate FROM ( SELECT /*+ INDEX_DESC(userinfo PK_USERINFO) */ ROWNUM AS rn, u_id, u_name, u_email, u_zip_code, u_addr, u_addrdetail, u_phone, u_regdate FROM userinfo
WHERE
ROWNUM <= (#{pageNum} * #{amount})
) WHERE RN > (#{pageNum} - 1) * #{amount}
